package test.jiaojiang.leetcode.onlinejudge;

import utils.Test;

import java.lang.reflect.InvocationTargetException;
import java.lang.reflect.Method;
import java.util.ArrayList;
import java.util.List;

/**
 * @author: mizhang
 * @time: 2/11/15 11:56 PM
 */
public class RestoreIPAddresses {
    public static void test(String packageName, String className, String methodName) throws InvocationTargetException, IllegalAccessException {
        try {
            Method method = Class.forName(packageName + "." + className).getMethod(methodName, String.class);
            List<String> expected = new ArrayList<String>();
            expected.add("255.255.11.135");
            expected.add("255.255.111.35");
            Test.assertEquals(expected, method.invoke(null, "25525511135"));

            expected.clear();
            Test.assertEquals(expected, method.invoke(null, "0279245587303"));
            Test.assertEquals(expected, method.invoke(null, ""));

            expected.add("0.10.0.10");
            expected.add("0.100.1.0");
            Test.assertEquals(expected, method.invoke(null, "010010"));

        } catch (Exception e) {
            System.err.println("Cannot find method " + methodName + " for class " + packageName + "." + className);
            e.printStackTrace();
        }
    }
}
